import React, { useState, useRef } from 'react';
import { UploadCloud, FileText, X, Database, Link, ShieldCheck, Activity, Lock, Server } from 'lucide-react';
import '../styles/Dashboard.css';

const FileUpload = ({ onFileChange, onAnalyze }) => {
  const [activeTab, setActiveTab] = useState('upload'); 
  const [dragActive, setDragActive] = useState(false);
  const [fileName, setFileName] = useState(null);
  const inputRef = useRef(null);

  // --- Handlers ---
  const handleDrag = (e) => {
    e.preventDefault(); e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") setDragActive(true);
    else if (e.type === "dragleave") setDragActive(false);
  };

  const handleDrop = (e) => {
    e.preventDefault(); e.stopPropagation();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) handleFile(e.dataTransfer.files[0]);
  };

  const handleChange = (e) => {
    if (e.target.files && e.target.files[0]) handleFile(e.target.files[0]);
  };

  const handleFile = (file) => {
    if (file.type !== "text/csv" && !file.name.endsWith('.csv')) {
      alert("Please upload a CSV file.");
      return;
    }
    setFileName(file.name);
    onFileChange({ target: { files: [file] } }); 
  };

  return (
    <div className="upload-container-wrapper">
      {/* 1. High-Tech Header */}
      <div className="governance-badge">
        <ShieldCheck size={16} />
        <span>Secure Ingestion Gateway <span style={{opacity:0.5}}>|</span> v2.4.0</span>
      </div>

      {/* 2. Tabs */}
      <div className="ingest-tabs">
        <button className={`tab-btn ${activeTab === 'upload' ? 'active' : ''}`} onClick={() => setActiveTab('upload')}>
          <UploadCloud size={16} /> File Upload
        </button>
        <button className={`tab-btn ${activeTab === 'api' ? 'active' : ''}`} onClick={() => setActiveTab('api')}>
          <Database size={16} /> Live Stream
        </button>
      </div>

      <div 
        className={`upload-box ${dragActive ? "drag-active" : ""}`} 
        onDragEnter={handleDrag} onDragLeave={handleDrag} onDragOver={handleDrag} onDrop={handleDrop}
      >
        {activeTab === 'upload' ? (
          <>
            {/* THIS IS THE UGLY INPUT - IT MUST HAVE THE HIDDEN CLASS */}
            <input ref={inputRef} type="file" className="file-input-hidden" onChange={handleChange} accept=".csv" />
            
            {!fileName ? (
              <>
                <div className="upload-icon-wrapper" style={{ 
                    background: 'rgba(255,255,255,0.05)', 
                    padding: '20px', 
                    borderRadius: '50%',
                    border: '1px solid rgba(255,255,255,0.1)' 
                }}>
                    <UploadCloud size={48} color={dragActive ? "#00ff88" : "#8b9bb4"} />
                </div>
                <div>
                    <h3>Drop Transaction Data</h3>
                    <p className="subtext">CSV Encrypted Upload</p>
                </div>
                {/* This button clicks the hidden input above */}
                <button className="secondary-btn" onClick={() => inputRef.current.click()}>Browse Local Drive</button>
              </>
            ) : (
              <div className="file-preview">
                <FileText size={40} color="#00f0ff" />
                <div className="file-info">
                  <h4>{fileName}</h4>
                  <span className="success-tag">Ready to Audit</span>
                </div>
                <button className="icon-btn" onClick={() => { setFileName(null); onFileChange({ target: { files: [] } }); }}>
                  <X size={20} />
                </button>
              </div>
            )}
          </>
        ) : (
          // API VIEW
          <div className="api-form">
            <div className="input-group">
              <label><Link size={14}/> Endpoint URL</label>
              <input type="text" placeholder="wss://api.visa.com/stream/v1" />
            </div>
            <div className="input-group">
              <label><Lock size={14}/> Bearer Token</label>
              <input type="password" placeholder="••••••••••••••••" />
            </div>
          </div>
        )}

        {(fileName || activeTab === 'api') && (
          <button className="primary-btn pulse-anim" onClick={onAnalyze}>
            Initialize Agent
          </button>
        )}
      </div>

      {/* 3. Tech Footer */}
      <div className="system-footer">
        <div className="sys-stat">
            <span className="sys-label"><Activity size={10}/> Latency</span>
            <span className="sys-value">24ms</span>
        </div>
        <div className="sys-stat">
            <span className="sys-label"><Server size={10}/> Region</span>
            <span className="sys-value">ap-south-1</span>
        </div>
        <div className="sys-stat">
            <span className="sys-label"><ShieldCheck size={10}/> Protocol</span>
            <span className="sys-value">TLS 1.3</span>
        </div>
      </div>
    </div>
  );
};

export default FileUpload;
