import React, { useState, useRef } from 'react';
import { UploadCloud, FileText, X, Database, Link, ShieldCheck } from 'lucide-react';
import '../styles/Dashboard.css';

const FileUpload = ({ onFileChange, onAnalyze }) => {
  const [activeTab, setActiveTab] = useState('upload'); // 'upload' or 'api'
  const [dragActive, setDragActive] = useState(false);
  const [fileName, setFileName] = useState(null);
  const inputRef = useRef(null);

  // --- Drag & Drop Handlers ---
  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFile(e.dataTransfer.files[0]);
    }
  };

  const handleChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      handleFile(e.target.files[0]);
    }
  };

  const handleFile = (file) => {
    // Basic validation
    if (file.type !== "text/csv" && !file.name.endsWith('.csv')) {
      alert("Please upload a CSV file.");
      return;
    }
    setFileName(file.name);
    // Create a fake event object to maintain compatibility with App.js
    onFileChange({ target: { files: [file] } }); 
  };

  return (
    <div className="upload-container-wrapper">
      {/* 1. Governance Badge (Requirement: Strict Governance) */}
      <div className="governance-badge">
        <ShieldCheck size={16} />
        <span>Secure Ingestion Gateway</span>
      </div>

      {/* 2. Tabs (Requirement: Versatile Inputs) */}
      <div className="ingest-tabs">
        <button 
          className={`tab-btn ${activeTab === 'upload' ? 'active' : ''}`} 
          onClick={() => setActiveTab('upload')}
        >
          <UploadCloud size={18} /> File Upload
        </button>
        <button 
          className={`tab-btn ${activeTab === 'api' ? 'active' : ''}`} 
          onClick={() => setActiveTab('api')}
        >
          <Database size={18} /> Connect API
        </button>
      </div>

      <div 
        className={`upload-box ${dragActive ? "drag-active" : ""}`} 
        onDragEnter={handleDrag} 
        onDragLeave={handleDrag} 
        onDragOver={handleDrag} 
        onDrop={handleDrop}
      >
        {activeTab === 'upload' ? (
          /* FILE VIEW */
          <>
            <input 
              ref={inputRef}
              type="file" 
              className="file-input-hidden" 
              onChange={handleChange} 
              accept=".csv" 
            />
            
            {!fileName ? (
              <>
                <UploadCloud size={64} color={dragActive ? "#00e676" : "#666"} />
                <h3>Drag & Drop Transaction Data</h3>
                <p className="subtext">Supports CSV, Excel, Parquet</p>
                <button className="secondary-btn" onClick={() => inputRef.current.click()}>
                  Browse Files
                </button>
              </>
            ) : (
              <div className="file-preview">
                <FileText size={48} color="#00bcd4" />
                <div className="file-info">
                  <h4>{fileName}</h4>
                  <span className="success-tag">Ready to Audit</span>
                </div>
                <button className="icon-btn" onClick={() => { setFileName(null); onFileChange({ target: { files: [] } }); }}>
                  <X size={20} />
                </button>
              </div>
            )}
          </>
        ) : (
          /* API VIEW (Requirement: API Source) */
          <div className="api-form">
            <div className="input-group">
              <label><Link size={14}/> API Endpoint URL</label>
              <input type="text" placeholder="https://api.gateway.visa.com/v1/transactions" />
            </div>
            <div className="input-group">
              <label><ShieldCheck size={14}/> Auth Token (Bearer)</label>
              <input type="password" placeholder="sk_live_..." />
            </div>
            <div className="api-note">
              *Connection is established via secure tunnel. Metadata only.
            </div>
          </div>
        )}

        {/* Analyze Button */}
        {(fileName || activeTab === 'api') && (
          <button className="primary-btn pulse-anim" onClick={onAnalyze}>
            Initialize Quality Agent
          </button>
        )}
      </div>
    </div>
  );
};

export default FileUpload;
