import React from 'react';
import { Wrench, CheckCircle } from 'lucide-react';
import { getFixSuggestions } from '../services/api'; // <--- IMPORT THIS
import '../styles/Dashboard.css';

const Recommendations = ({ items }) => {
  // Fallback items if backend is empty
  const list = items && items.length > 0 ? items : [
    "Standardize date format",
    "Check for missing names",
    "Remove duplicate rows"
  ];

  // Handler for the button click
  const handleFixClick = async (issue) => {
    // 1. Call API
    // We use a simple confirm/alert flow for this demo
    const suggestion = await getFixSuggestions(issue);
    
    // 2. Show the "Simple" explanation
    alert(`BEGINNER FIX GUIDE for: "${issue}"\n\n${suggestion}`);
  };

  return (
    <div className="card recommendations-card">
      <div className="card-header">
        <Wrench size={24} color="#ffc400" />
        <h3>Suggested Remediation</h3>
      </div>
      <div className="rec-list">
        {list.map((item, index) => (
          <div key={index} className="rec-item">
            <CheckCircle size={18} className="rec-icon" />
            <span>{item}</span>
            {/* 3. Attach the handler here */}
            <button 
              className="fix-btn" 
              onClick={() => handleFixClick(item)}
            >
              Apply Fix
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Recommendations;
