import React, { useState } from 'react';
import { uploadDataset } from './services/api';

// Components
import FileUpload from './components/FileUpload';
import LoadingState from './components/LoadingState';
import QualityGauge from './components/QualityGauge';
import DimensionBars from './components/DimensionBars';
import InsightCard from './components/InsightCard';
import Recommendations from './components/Recommendations';

import './styles/Dashboard.css';

function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);

  // Wrapper to match FileUpload component signature
  const handleFileChange = (e) => setFile(e.target.files[0]);

  const handleAnalyze = async () => {
    if (!file) return;
    setLoading(true);
    try {
      const result = await uploadDataset(file);
      setData(result);
    } catch (error) {
      console.error("Analysis failed", error);
      alert("Backend connection failed. Check console.");
    }
    setLoading(false);
  };

  return (
    <div className="app-container">
      <header className="navbar">
        <h2>DataQuality<span className="ai-text">AI</span></h2>
      </header>

      <main className="main-content">
        {/* State 1: Initial Upload Screen */}
        {!data && !loading && (
          <FileUpload onFileChange={handleFileChange} onAnalyze={handleAnalyze} />
        )}

        {/* State 2: Loading Animation */}
        {loading && <LoadingState />}

        {/* State 3: The Dashboard */}
        {data && (
          <div className="dashboard-grid">
            {/* Left Column: Metrics */}
            <div className="metrics-col">
              <div className="card">
                <QualityGauge score={data.dqs} />
              </div>
              <DimensionBars dimensions={data.dimensions} />
            </div>

            {/* Right Column: AI & Recommendations */}
            <div className="insights-col">
              <InsightCard insights={data.insights} />
              {/* Pass backend recommendations or null to trigger defaults */}
              <Recommendations items={data.recommendations} /> 
            </div>
            
            {/* Reset Button to go back */}
            <button className="reset-btn" onClick={() => { setData(null); setFile(null); }}>
              Analyze Another Dataset
            </button>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
