import axios from 'axios';

// NOTE: Your backend runs on /evaluate, not /api/upload
const API_URL = 'http://localhost:5000'; 

export const uploadDataset = async (file) => {
  const formData = new FormData();
  
  // 1. Match the key name expected by: file = request.files["file"]
  formData.append('file', file);
  
  // 2. Match the key name expected by: context = request.form.get("context")
  // We hardcode 'fraud' for the demo, or you can pass it as an argument
  formData.append('context', 'fraud'); 
  
  try {
    const response = await axios.post(`${API_URL}/evaluate`, formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    return response.data;
  } catch (error) {
    console.error("API Error:", error);
    throw error;
  }
};
