import React, { useState } from 'react';
import { uploadDataset } from './services/api';

// Components
import FileUpload from './components/FileUpload';
import LoadingState from './components/LoadingState';
import QualityGauge from './components/QualityGauge';
import DimensionBars from './components/DimensionBars';
import InsightCard from './components/InsightCard';
import Recommendations from './components/Recommendations';

import './styles/Dashboard.css';

// ... imports stay the same ...

function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => setFile(e.target.files[0]);

  const handleAnalyze = async () => {
    // Logic for API Tab vs File Tab
    if (!file) {
        // Fallback for API tab demo (since we don't have a real stream source yet)
        setLoading(true);
        setTimeout(() => {
             setData({
                dqs: 85,
                dimensions: { completeness: 90, uniqueness: 80, validity: 85 },
                insights: "API Stream Analysis: Data velocity is stable. Detected 98% schema compliance.",
                recommendations: ["Monitor API rate limits", "Check timestamp precision"]
             });
             setLoading(false);
        }, 2000);
        return;
    }

    setLoading(true);
    try {
      const result = await uploadDataset(file);
      
      // --- DATA MAPPING (Backend -> Frontend) ---
      // Your Backend returns: { dqs, scores, explanation, recommendations }
      // Your Frontend needs:  { dqs, dimensions, insights, recommendations }
      
      const mappedData = {
        dqs: result.dqs,
        dimensions: result.scores,       // Map 'scores' -> 'dimensions'
        insights: result.explanation,    // Map 'explanation' -> 'insights'
        recommendations: result.recommendations
      };

      setData(mappedData);
    } catch (error) {
      console.error("Analysis failed", error);
      alert("Backend connection failed. Check console.");
    }
    setLoading(false);
  };

  // ... return statement stays the same ...

  return (
    <div className="app-container">
      <header className="navbar">
        <div className="nav-brand">
          <h2>DataQuality<span className="ai-text">AI</span></h2>
        </div>
        
        {/* NEW: Governance Status Indicator */}
        <div className="nav-status">
          <div className="status-item">
            <span className="status-dot"></span>
            Zero-Persistence Mode: <strong>ACTIVE</strong>
          </div>
        </div>
      </header>

      <main className="main-content">
        {/* State 1: Initial Upload Screen */}
        {!data && !loading && (
          <FileUpload onFileChange={handleFileChange} onAnalyze={handleAnalyze} />
        )}

        {/* State 2: Loading Animation */}
        {loading && <LoadingState />}

        {/* State 3: The Dashboard */}
        {data && (
          <div className="dashboard-grid">
            {/* Left Column: Metrics */}
            <div className="metrics-col">
              <div className="card">
                <QualityGauge score={data.dqs} />
              </div>
              <DimensionBars dimensions={data.dimensions} />
            </div>

            {/* Right Column: AI & Recommendations */}
            <div className="insights-col">
              <InsightCard insights={data.insights} />
              {/* Pass backend recommendations or null to trigger defaults */}
              <Recommendations items={data.recommendations} /> 
            </div>
            
            {/* Reset Button to go back */}
            <button className="reset-btn" onClick={() => { setData(null); setFile(null); }}>
              Analyze Another Dataset
            </button>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
