import React, { useState } from 'react';
import { uploadDataset } from './services/api';

// Components
import FileUpload from './components/FileUpload';
import LoadingState from './components/LoadingState';
import QualityGauge from './components/QualityGauge';
import DimensionBars from './components/DimensionBars';
import InsightCard from './components/InsightCard';
import Recommendations from './components/Recommendations';
import ChatAssistant from './components/ChatAssistant'; // Import Chatbot

import './styles/Dashboard.css';

function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);

  // New state to remember selected profile
  const [currentProfile, setCurrentProfile] = useState('analytics');

  const handleFileChange = (uploadedFile) => {
    setFile(uploadedFile);
  };

  const handleAnalyze = async (selectedProfile) => {
    if (!file) return;

    setLoading(true);
    setCurrentProfile(selectedProfile); // Store for UI display

    try {
      // Pass both file AND profile to the API
      const result = await uploadDataset(file, selectedProfile);
      
      const mappedData = {
        dqs: result.dqs,
        dimensions: result.dimensions, 
        insights: result.insights,
        recommendations: result.recommendations
      };

      setData(mappedData);
    } catch (error) {
      console.error("Analysis failed", error);
      alert("Backend connection failed. Check console.");
    }
    setLoading(false);
  };

}

export default App;
