import React, { useState } from 'react';
import { uploadDataset } from './services/api';

// Components
import FileUpload from './components/FileUpload';
import LoadingState from './components/LoadingState';
import QualityGauge from './components/QualityGauge';
import DimensionBars from './components/DimensionBars';
import InsightCard from './components/InsightCard';
import Recommendations from './components/Recommendations';
import ChatAssistant from './components/ChatAssistant'; // Import Chatbot

import './styles/Dashboard.css';

function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);

  // New state to remember selected profile
  const [currentProfile, setCurrentProfile] = useState('analytics');

  const handleFileChange = (uploadedFile) => {
    setFile(uploadedFile);
  };

  const handleAnalyze = async (selectedProfile) => {
    if (!file) return;

    setLoading(true);
    setCurrentProfile(selectedProfile); // Store for UI display

    try {
      // Pass both file AND profile to the API
      const result = await uploadDataset(file, selectedProfile);
      
      const mappedData = {
        dqs: result.dqs,
        dimensions: result.dimensions, 
        insights: result.insights,
        recommendations: result.recommendations
      };

      setData(mappedData);
    } catch (error) {
      console.error("Analysis failed", error);
      alert("Backend connection failed. Check console.");
    }
    setLoading(false);
  };

  return (
    <div className="app-container">
      <header className="navbar">
        <div className="nav-brand">
          <h2>DataQuality<span className="ai-text">AI</span></h2>
        </div>
        <div className="nav-status">
          <div className="status-item">
            <span className="status-dot"></span>
            Agent Mode: <strong>{data ? currentProfile.toUpperCase() : 'STANDBY'}</strong>
          </div>
        </div>
      </header>

      <main className="main-content">
        {!data && !loading && (
          <FileUpload onFileChange={handleFileChange} onAnalyze={handleAnalyze} />
        )}

        {loading && <LoadingState />}

        {data && (
          <div className="dashboard-grid">
            <div className="metrics-col">
              <div className="card">
                <QualityGauge score={data.dqs} />
              </div>
              <DimensionBars dimensions={data.dimensions} />
            </div>

            <div className="insights-col">
              <InsightCard insights={data.insights} />
              <Recommendations items={data.recommendations} /> 
              <button className="secondary-btn" onClick={() => { setData(null); setFile(null); }} style={{marginTop:'1rem', width:'100%'}}>
                Analyze Another Dataset
              </button>
            </div>
          </div>
        )}
      </main>
      
      {/* CHATBOT: Always visible in bottom right */}
      <ChatAssistant />
    </div>
  );
}

export default App;
