import React, { useState, useRef } from 'react';
import { UploadCloud, FileText, X } from 'lucide-react';
import '../styles/Dashboard.css'; // Ensure CSS is imported

const FileUpload = ({ onFileChange, onAnalyze }) => {
  const [dragActive, setDragActive] = useState(false);
  const [fileName, setFileName] = useState(null);
  const inputRef = useRef(null);

  // Handle Drag Events
  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  // Handle Drop
  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      const file = e.dataTransfer.files[0];
      handleFile(file);
    }
  };

  // Handle Manual Click
  const handleChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      handleFile(e.target.files[0]);
    }
  };

  const handleFile = (file) => {
    if (file.type !== "text/csv" && !file.name.endsWith('.csv')) {
      alert("Please upload a CSV file.");
      return;
    }
    setFileName(file.name);
    // Create a fake event object to maintain compatibility with App.js
    onFileChange({ target: { files: [file] } }); 
  };

  const clearFile = () => {
    setFileName(null);
    onFileChange({ target: { files: [] } });
    if (inputRef.current) inputRef.current.value = "";
  };

  return (
    <div 
      className={`upload-box ${dragActive ? "drag-active" : ""}`} 
      onDragEnter={handleDrag} 
      onDragLeave={handleDrag} 
      onDragOver={handleDrag} 
      onDrop={handleDrop}
    >
      <input 
        ref={inputRef}
        type="file" 
        className="file-input-hidden" 
        onChange={handleChange} 
        accept=".csv" 
      />
      
      {!fileName ? (
        <>
          <UploadCloud size={64} color={dragActive ? "#00e676" : "#666"} />
          <h3>Drag & Drop Transaction CSV</h3>
          <p className="subtext">or click to browse</p>
          <button className="secondary-btn" onClick={() => inputRef.current.click()}>
            Browse Files
          </button>
        </>
      ) : (
        <div className="file-preview">
          <FileText size={48} color="#00bcd4" />
          <div className="file-info">
            <h4>{fileName}</h4>
            <span className="success-tag">Ready to Audit</span>
          </div>
          <button className="icon-btn" onClick={clearFile}>
            <X size={20} />
          </button>
        </div>
      )}

      {fileName && (
        <button className="primary-btn pulse-anim" onClick={onAnalyze}>
          Run Audit Agent
        </button>
      )}
    </div>
  );
};

export default FileUpload;
