import axios from 'axios';

const API_URL = 'http://127.0.0.1:5000'; 

export const uploadDataset = async (file, context) => {
  const formData = new FormData();
  formData.append('file', file);
  formData.append('context', context); // Now dynamic!
  
  try {
    const response = await axios.post(`${API_URL}/evaluate`, formData, {
      headers: { 'Content-Type': 'multipart/form-data' },
    });
    return response.data;
  } catch (error) {
    console.error("API Error:", error);
    throw error;
  }
};

export const sendChatMessage = async (message) => {
  try {
    const response = await axios.post(`${API_URL}/chat`, { message });
    return response.data.reply;
  } catch (error) {
    console.error("Chat Error:", error);
    return "Error connecting to AI.";
  }
};
// ... existing imports and functions

export const getFixSuggestions = async (issue) => {
  try {
    const response = await axios.post(`${API_URL}/fix`, { issue });
    return response.data.fix;
  } catch (error) {
    console.error("Fix Error:", error);
    return "Unable to connect to AI for help.";
  }
};

export const generateReport = async (data) => {
    try {
        const response = await axios.post(`${API_URL}/generate_report`, data, {
            responseType: 'blob' 
        });
        return response;
    } catch (error) {
        console.error("Error generating report", error);
        throw error;
    }
};
